INSERT INTO apis.apis (id, name, description, base_url, documentation, response_type, collection_date, authentication, version, institution_id) VALUES (16, 'Legislativo', 'Dados sobre a camara de deputados federal', 'https://dadosabertos.camara.leg.br/swagger/api.html', 'https://dadosabertos.camara.leg.br/api', 'JSON', '2023-09-06', 'N', '0.4.144', 12);
INSERT INTO apis.institutions (id, name, acronym) VALUES (12, 'Câmara dos Deputados', 'CD');
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (426, 16, 'Blocos', 'https://dadosabertos.camara.leg.br/api/v2/blocos', 'Lista de dados sobre os blocos partidários', 'https://dadosabertos.camara.leg.br/api/v2/blocos?ordem=ASC&ordenarPor=nome', 426);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (427, 16, 'Bloco por ID', 'https://dadosabertos.camara.leg.br/api/v2/blocos/{id}', 'Informações sobre um bloco partidário específico', 'https://dadosabertos.camara.leg.br/api/v2/blocos/584', 427);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (428, 16, 'Deputados', 'https://dadosabertos.camara.leg.br/api/v2/deputados', 'Listagem e busca dos Deputados', 'https://dadosabertos.camara.leg.br/api/v2/deputados?ordem=ASC&ordenarPor=nome', 428);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (429, 16, 'Deputado por id', 'https://dadosabertos.camara.leg.br/api/v2/deputados/{id}', 'Retorna os dados cadastrais de um parlamentar identificado por {id} que, em algum momento da história e por qualquer período, entrou em exercício na Câmara.', 'https://dadosabertos.camara.leg.br/api/v2/deputados/220645', 429);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (430, 16, 'Depesas de um deputado', 'https://dadosabertos.camara.leg.br/api/v2/deputados/{id}/despesas', 'Dá acesso aos registros de pagamentos e reembolsos feitos pela Câmara em prol do deputado identificado por {id}, a título da Cota para Exercício da Atividade Parlamentar, a chamada "cota parlamentar".', 'https://dadosabertos.camara.leg.br/api/v2/deputados/220645/despesas?ordem=ASC&ordenarPor=ano', 430);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (431, 16, 'Discursos de um deputado', 'https://dadosabertos.camara.leg.br/api/v2/deputados/{id}/discursos', 'Retorna uma lista de informações sobre os pronunciamentos feitos pelo deputado identificado por {id} que tenham sido registrados, em quaisquer eventos, nos sistemas da Câmara.', 'https://dadosabertos.camara.leg.br/api/v2/deputados/220645/discursos?ordenarPor=dataHoraInicio&ordem=ASC', 431);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (432, 16, 'Eventos de um deputado', 'https://dadosabertos.camara.leg.br/api/v2/deputados/{id}/eventos', 'Retorna uma lista de objetos evento nos quais a participação do parlamentar identificado por {id} era ou é prevista.', 'https://dadosabertos.camara.leg.br/api/v2/deputados/220645/eventos?ordem=ASC&ordenarPor=dataHoraInicio', 432);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (433, 16, 'Frentes de um deputado', 'https://dadosabertos.camara.leg.br/api/v2/deputados/{id}/frentes', 'Retorna uma lista de informações básicas sobre as frentes parlamentares das quais o parlamentar identificado por {id} seja membro, ou, no caso de frentes existentes em legislaturas anteriores, tenha encerrado a legislatura como integrante.', 'https://dadosabertos.camara.leg.br/api/v2/deputados/220645/frentes', 433);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (434, 16, 'Historico - Deputados', 'https://dadosabertos.camara.leg.br/api/v2/deputados/{id}/historico', 'Retorna uma listagem com as diferentes situações de exercício parlamentar do deputado identificado por {id}, mesmo que a alteração registrada não tenha afetado o andamento do mandato.', 'https://dadosabertos.camara.leg.br/api/v2/deputados/220593/historico', 434);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (435, 16, 'Mandato Externo de um deputado', 'https://dadosabertos.camara.leg.br/api/v2/deputados/{id}/mandatoExterno', 'Retorna uma lista em que cada item traz informações básicas sobre um cargo para o qual o parlamentar identificado por {id} tenha sido eleito, em sua carreira política fora da Câmara dos Deputados. Estes dados são fornecidos pelo Tribunal Superior Eleitoral', 'https://dadosabertos.camara.leg.br/api/v2/deputados/220645/mandatoExterno', 435);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (436, 16, 'Ocupações de um deputado', 'https://dadosabertos.camara.leg.br/api/v2/deputados/{id}/ocupacoes', 'Enumera as atividades profissionais ou ocupacionais que o deputado identificado por {id} já teve em sua carreira e declarou à Câmara dos Deputados.', 'https://dadosabertos.camara.leg.br/api/v2/deputados/220645/ocupacoes', 436);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (437, 16, 'Deputado por id - Orgao', 'https://dadosabertos.camara.leg.br/api/v2/deputados/{id}/orgaos', 'Retorna uma lista de órgãos, como as comissões e procuradorias, dos quais o deputado identificado por {id} participa ou participou durante um intervalo de tempo.', 'https://dadosabertos.camara.leg.br/api/v2/deputados/220645/orgaos?ordem=ASC&ordenarPor=dataInicio', 437);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (438, 16, 'Profissoes de um deputado', 'https://dadosabertos.camara.leg.br/api/v2/deputados/{id}/profissoes', 'Retorna uma lista de dados sobre profissões que o parlamentar identificado por {id} declarou à Câmara que já exerceu ou que pode exercer pela sua formação e/ou experiência.', 'https://dadosabertos.camara.leg.br/api/v2/deputados/220645/profissoes', 438);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (439, 16, 'Lideres filtrando por id - Legistaturas', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas/{id}/lideres', 'Retorna uma lista de parlamentares que ocuparam cargos de liderança ao longo da legislatura {id}', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas/220645/lideres', 439);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (440, 16, 'Legislatura - Mesa filtrando por ID', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas/{id}/mesa', 'Retorna uma lista com dados básicos sobre todos os deputados que ocuparam algum posto na Mesa Diretora da Câmara em algum período de tempo dentro da legislatura identificada por {id}.', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas/220645/mesa', 440);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (441, 16, 'Codigo Situação Deputados', 'https://dadosabertos.camara.leg.br/api/v2/referencias/deputados/codSituacao', 'Retorna uma lista de siglas e descrições dos possíveis estados em que um deputado pode estar em relação ao seu exercício parlamentar: Exercício, Fim de Mandato, Afastado, etc.', 'https://dadosabertos.camara.leg.br/api/v2/referencias/deputados/codSituacao', 441);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (442, 16, 'Situações Deputado', 'https://dadosabertos.camara.leg.br/api/v2/referencias/situacoesDeputado', 'Retorna uma lista de siglas e descrições dos possíveis estados em que um deputado pode estar em relação ao seu exercício parlamentar: Exercício, Fim de Mandato, Afastado, etc.', 'https://dadosabertos.camara.leg.br/api/v2/referencias/situacoesDeputado', 442);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (443, 16, 'Eventos', 'https://dadosabertos.camara.leg.br/api/v2/eventos', 'Retorna uma lista cujos elementos trazem informações básicas sobre eventos dos órgãos legislativos da Câmara, previstos ou já ocorridos, em um certo intervalo de tempo.', 'https://dadosabertos.camara.leg.br/api/v2/eventos?ordem=ASC&ordenarPor=dataHoraInicio', 443);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (444, 16, 'Eventos filtrando por ID', 'https://dadosabertos.camara.leg.br/api/v2/eventos/{id}', 'Retorna um conjunto detalhado de informações sobre o evento da Câmara identificado por id.', 'https://dadosabertos.camara.leg.br/api/v2/eventos/69159', 444);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (445, 16, 'Eventos Deputados filtrando por ID', 'https://dadosabertos.camara.leg.br/api/v2/eventos/{id}/deputados', 'Retorna uma lista de dados resumidos sobre deputados participantes do evento identificado por {id}.', 'https://dadosabertos.camara.leg.br/api/v2/eventos/69159/deputados', 445);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (446, 16, 'Eventos - Orgaos filtrando por ID', 'https://dadosabertos.camara.leg.br/api/v2/eventos/{id}/orgaos', 'Retorna uma lista em que cada item é um conjunto mínimo de dados sobre o(s) órgão(s) responsável(veis) pelo evento identificado por {id}', 'https://dadosabertos.camara.leg.br/api/v2/eventos/69159/orgaos', 446);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (447, 16, 'Eventos- Pauta filtrando por ID', 'https://dadosabertos.camara.leg.br/api/v2/eventos/{id}/pauta', 'Se o evento {id} for de caráter deliberativo (uma reunião ordinária, por exemplo) este serviço retorna a lista de proposições previstas para avaliação pelos parlamentares. Cada item identifica, se as informações estiverem disponíveis, a proposição avaliada, o regime de preferência para avaliação, o ', 'https://dadosabertos.camara.leg.br/api/v2/eventos/69159/pauta', 447);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (448, 16, 'Eventos - Votações filtrando por ID', 'https://dadosabertos.camara.leg.br/api/v2/eventos/{id}}votacoes', 'Retorna uma lista de dados básicos sobre votações que tenham sido realizadas no evento identificado por {id}', 'https://dadosabertos.camara.leg.br/api/v2/eventos/69159/votacoes', 448);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (449, 16, 'Codigo Situação Evento', 'https://dadosabertos.camara.leg.br/api/v2/referencias/eventos/codSituacaoEvento', 'Retorna uma lista de identificadores numéricos, siglas e descrições dos estados em que eventos como uma reunião podem se encontrar, como Em Andamento, Cancelada e Encerrada.', 'https://dadosabertos.camara.leg.br/api/v2/referencias/eventos/codSituacaoEvento', 449);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (450, 16, 'Codigo Tipo de Evento', 'https://dadosabertos.camara.leg.br/api/v2/referencias/eventos/codTipoEvento', 'Retorna uma lista de identificadores numéricos, siglas e descrições dos tipos de eventos ocorridos na Câmara, tais como Audiência Pública, Comissão Geral e Palestra, entre outros.', 'https://dadosabertos.camara.leg.br/api/v2/referencias/eventos/codTipoEvento', 450);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (451, 16, 'Situações Eventos', 'https://dadosabertos.camara.leg.br/api/v2/referencias/situacoesEvento', 'Retorna uma lista de identificadores numéricos, siglas e descrições dos estados em que eventos como uma reunião podem se encontrar, como Em Andamento, Cancelada e Encerrada.', 'https://dadosabertos.camara.leg.br/api/v2/referencias/situacoesEvento', 451);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (452, 16, 'Tipos Eventos', 'https://dadosabertos.camara.leg.br/api/v2/referencias/tiposEvento', 'Retorna uma lista de identificadores numéricos, siglas e descrições dos tipos de eventos ocorridos na Câmara, tais como Audiência Pública, Comissão Geral e Palestra, entre outros.', 'https://dadosabertos.camara.leg.br/api/v2/referencias/tiposEvento', 452);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (453, 16, 'Frentes', 'https://dadosabertos.camara.leg.br/api/v2/frentes', 'Retorna uma lista de informações sobre uma frente parlamentar - um agrupamento oficial de parlamentares em torno de um determinado tema ou proposta.', 'https://dadosabertos.camara.leg.br/api/v2/frentes', 453);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (454, 16, 'Frentes filtrando por Id ', 'https://dadosabertos.camara.leg.br/api/v2/frentes/{id}', 'Este recurso traz informações detalhadas sobre a frente parlamentar identificada por {id}.', 'https://dadosabertos.camara.leg.br/api/v2/frentes/54537', 454);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (455, 16, 'Membros frentes por Id', 'https://dadosabertos.camara.leg.br/api/v2/frentes/{id}/membros', 'Uma lista dos deputados participantes da frente parlamentar identificada por {id} e os papéis que exerceram nessa frente (signatário, coordenador ou presidente).', 'https://dadosabertos.camara.leg.br/api/v2/frentes/54537/membros', 455);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (456, 16, 'Grupos', 'https://dadosabertos.camara.leg.br/api/v2/grupos', 'Este endpoint retorna uma lista em que cada item representa um dos grupos interparlamentares em que a Câmara teve representantes. As informações incluem nome, a resolução que criou o grupo e seu ano de publicação, e as mais recentes informações sobre situação, presidente e ofício de instalação.', 'https://dadosabertos.camara.leg.br/api/v2/grupos', 456);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (457, 16, 'Grupos filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/grupos/{id}', 'Todo o conjunto de informações disponíveis sobre o grupo parlamentar identificado por {id}. Além dos retornados por /grupos, há dados sobre o projeto de resolução que levou à criação do grupo, identificadores do mais recente documento e/ou ofício de instalação do grupo, datas de sua apresentação e d', 'https://dadosabertos.camara.leg.br/api/v2/grupos/1', 457);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (458, 16, 'Histórico- Grupos por id', 'https://dadosabertos.camara.leg.br/api/v2/grupos/{id}/historico', 'Uma lista contendo todos os "retratos" das informações sujeitas a mudanças sobre o grupo parlamentar identificado por {id} — por exemplo, todos os ofícios de instalação dos grupos apresentados a cada legislatura desde sua criação, e eventuais substituições de presidentes.', 'https://dadosabertos.camara.leg.br/api/v2/grupos/1/historico', 458);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (459, 16, 'Membros - Grupos por id', 'https://dadosabertos.camara.leg.br/api/v2/grupos/{id}/membros', 'Retorna uma lista de deputados ou senadores que são ou foram participantes do grupo interparlamentar identificado por {id}. Podem ser utilizados os parâmetros dataInicio e/ou dataFim para definir de qual período se deseja saber quem foram os participantes.', 'https://dadosabertos.camara.leg.br/api/v2/grupos/1/membros?ordem=ASC&ordenarPor=idLegislatura', 459);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (460, 16, 'Legislatura', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas?ordem=DESC&ordenarPor=id', 'Este serviço retorna uma lista em que cada item contém as informações básicas sobre um desses períodos.', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas?ordem=DESC&ordenarPor=id', 460);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (461, 16, 'Legisltaturas filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas/{id}', 'Retorna informações adicionais sobre o período de atividades da Câmara identificado por {id}.', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas/57', 461);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (462, 16, 'Lideres - Legislaturas filtrando poir id', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas/{id}/lideres', 'Retorna uma lista de parlamentares que ocuparam cargos de liderança ao longo da legislatura {id}. Cada item identifica um parlamentar, uma bancada (partido, bloco ou lideranças de situação e oposição), o título de liderança exercido e o período de exercício do parlamentar nesta posição.', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas/57/lideres', 462);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (463, 16, 'Mesa - Legislaturas filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas/{id}/mesa', 'Retorna uma lista com dados básicos sobre todos os deputados que ocuparam algum posto na Mesa Diretora da Câmara em algum período de tempo dentro da legislatura identificada por {id}.', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas/57/mesa', 463);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (464, 16, 'Partidos', 'https://dadosabertos.camara.leg.br/api/v2/partidos', 'Retorna uma lista de dados básicos sobre os partidos políticos que têm ou já tiveram deputados na Câmara.', 'https://dadosabertos.camara.leg.br/api/v2/partidos?ordem=ASC&ordenarPor=sigla', 464);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (465, 16, 'Partidos - Filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/partidos/{id}', 'Retorna informações detalahadas sobre um partido', 'https://dadosabertos.camara.leg.br/api/v2/partidos/36839', 465);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (466, 16, 'Lideres - Partidos filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/partidos/{id}/lideres', 'Retorna uma lista de deputados que ocupam ou ocuparam cargos de líder ou vice-líder do partido {id} num intervalo de tempo opcional, com a identificação do cargo e o período em que o tiveram.', 'https://dadosabertos.camara.leg.br/api/v2/partidos/36839/lideres', 466);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (467, 16, 'Membros - Partidos filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/partidos/{id}/membros', 'Retorna uma lista de deputados que estão ou estiveram em exercício pelo partido {id}.', 'https://dadosabertos.camara.leg.br/api/v2/partidos/36839/membros', 467);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (468, 16, 'Proposições', 'https://dadosabertos.camara.leg.br/api/v2/proposicoes?ordem=ASC&ordenarPor=id', 'Lista de informações básicas sobre projetos de lei, resoluções, medidas provisórias, emendas, pareceres e todos os outros tipos de proposições na Câmara.', 'https://dadosabertos.camara.leg.br/api/v2/proposicoes?ordem=ASC&ordenarPor=id', 468);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (469, 16, 'Proposições filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/proposicoes/{id}', 'Informações detalhas de uma proposição específica', 'https://dadosabertos.camara.leg.br/api/v2/proposicoes/276180', 469);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (470, 16, 'Autores - Proposiçoes filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/proposicoes/{id}}/autores', 'Retorna uma lista em que cada item identifica uma pessoa ou entidade que é autora da proposição identificada por {id}. Além de deputados, também podem ser autores de proposições os senadores, a sociedade civil, assembleias legislativas e os poderes Executivo e Judiciário.', 'https://dadosabertos.camara.leg.br/api/v2/proposicoes/276180/autores', 470);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (471, 16, 'Relacionadas - Proposições filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/proposicoes/{id}/relacionadas', 'Lista de informações básicas sobre proposições que de alguma forma se relacionam com a proposição identificada por {id}, como pareceres, requerimentos, substitutivos, etc.', 'https://dadosabertos.camara.leg.br/api/v2/proposicoes/276180/relacionadas', 471);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (472, 16, 'Temas - Proposições filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/proposicoes/{id/temas', 'Lista em que cada item traz informações sobre uma área temática à qual a proposição identificada por {id} se relaciona, segundo classificação oficial do Centro de Documentação e Informação da Câmara', 'https://dadosabertos.camara.leg.br/api/v2/proposicoes/276180/temas', 472);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (473, 16, 'Tramitações - Proposições filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/proposicoes/{id}/tramitacoes', 'Lista que traz, como cada item, um "retrato" de informações que podem ser alteradas a cada etapa de tramitação na vida de uma proposição (como regime de tramitação e situação) e informações sobre o que causou esse novo estado.', 'https://dadosabertos.camara.leg.br/api/v2/proposicoes/276180/tramitacoes', 473);
