INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (512, 16, 'Votações filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/votacoes/{id}', 'Retorna um conjunto detalhado de dados sobre a votação identificada por {id}, tais como as proposições que podem ter sido o objeto da votação e os efeitos de tramitação de outras proposições que eventualmente tenham sido cadastrados em consequência desta votação.', 'https://dadosabertos.camara.leg.br/api/v2/votacoes/2372071-13', 512);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (513, 16, 'Orientações - Votações filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/votacoes/{id}/orientacoes', 'Se a votação identificada por {id} teve orientações, este recurso retorna uma lista em que cada item contém os identificadores de um partido, bloco ou liderança, e o posicionamento ou voto que foi recomendado aos seus parlamentares.', 'https://dadosabertos.camara.leg.br/api/v2/votacoes/2372071-13/orientacoes', 513);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (514, 16, 'Votos- Votações Filtranto por id', 'https://dadosabertos.camara.leg.br/api/v2/votacoes/{id}/votos', 'Se {id} é o identificador de uma votação da Câmara nominal que não tenha sido secreta, este endpoint retorna uma lista em que cada item contém os identificadores básicos de um deputado e o voto ou posicionamento que ele registrou.', 'https://dadosabertos.camara.leg.br/api/v2/votacoes/2372071-13/votos', 514);
