INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (517, 16, 'Eventos- Orgãos filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/orgaos/{id}}/eventos?ordem=ASC&ordenarPor=dataHoraInicio', 'Retorna uma lista de informações resumidas dos eventos realizados (ou a realizar) pelo órgão legislativo identificado por {id}.', 'https://dadosabertos.camara.leg.br/api/v2/orgaos/81/eventos?ordem=ASC&ordenarPor=dataHoraInicio', 517);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (518, 16, 'Membros - Orgãos filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/orgaos/{id}/membros', 'Retorna uma lista de dados resumidos que identificam cada parlamentar e o cargo ou posição que ocupa ou ocupou no órgão parlamentar identificado por {id} durante um certo período de tempo.', 'https://dadosabertos.camara.leg.br/api/v2/orgaos/4/membros', 518);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (519, 16, 'Votações - Orgãos filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/orgaos/{id}/votacoes?ordem=DESC&ordenarPor=dataHoraRegistro', 'Retorna uma lista de dados básicos de votações que tenham sido realizadas em eventos realizados no órgão {id}.', 'https://dadosabertos.camara.leg.br/api/v2/orgaos/4/votacoes?ordem=DESC&ordenarPor=dataHoraRegistro', 519);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (520, 16, 'Codigo Situação- Orgãos filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/referencias/orgaos/codSituacao', 'Retorna uma lista de identificadores numéricos, títulos e descrições das situações possíveis para órgãos em operação na Câmara, como Em funcionamento, Extinta, Pronta para criação, etc.', 'https://dadosabertos.camara.leg.br/api/v2/referencias/orgaos/codSituacao', 520);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (521, 16, 'Situações Orgão- Orgãos filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/referencias/situacoesOrgao', 'Retorna uma lista de identificadores numéricos, títulos e descrições das situações possíveis para órgãos em operação na Câmara, como Em funcionamento, Extinta, Pronta para criação, etc.', 'https://dadosabertos.camara.leg.br/api/v2/referencias/situacoesOrgao', 521);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (522, 16, 'Líderes - Legislaturas filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas/57/lideres', 'Retorna uma lista de parlamentares que ocuparam cargos de liderança ao longo da legislatura {id}. Cada item identifica um parlamentar, uma bancada (partido, bloco ou lideranças de situação e oposição), o título de liderança exercido e o período de exercício do parlamentar nesta posição.', 'https://dadosabertos.camara.leg.br/api/v2/legislaturas/57/lideres', 522);
INSERT INTO apis.api_endpoints (id, api_id, name, url, description, example, api_responses_id) VALUES (523, 16, 'Líderes - Partidos filtrando por id', 'https://dadosabertos.camara.leg.br/api/v2/partidos/{id}/lideres', 'Retorna uma lista de deputados que ocupam ou ocuparam cargos de líder ou vice-líder do partido {id} num intervalo de tempo opcional, com a identificação do cargo e o período em que o tiveram.', 'https://dadosabertos.camara.leg.br/api/v2/partidos/36844/lideres', 523);
