INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (963, 'id', 'array[integer]', null, 'Número(s) identificador(es) de um ou mais bloco(s), separados por vírgulas.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (964, 'idLegislatura', 'array[integer]', null, 'Número(s) identificador(es) de uma ou mais legislatura(s), separados por vírgulas. Se presente, faz com que sejam retornados todos os blocos que existiram nessa(s) legislatura(s).');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (965, 'pagina', 'INTEGER', null, 'Número da página de resultados, a partir de 1, que se deseja obter com a requisição, contendo o número de itens definido pelo parâmetro itens. Se omitido, assume o valor 1.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (966, 'itens', 'INTEGER', null, 'Número máximo de itens na página que se deseja obter com esta requisição.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (967, 'ordem', 'string', null, 'O sentido da ordenação: asc para A a Z ou 0 a 9, e desc para Z a A ou 9 a 0.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (968, 'ordenarPor', 'string', null, 'Nome do campo pelo qual a lista será ordenada: idLegislatura, id ou nome.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (969, 'id', 'INTEGER', null, 'Número identificador do bloco de partidos.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (970, 'nome', 'string', null, 'Parte nome parlamentar.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (971, 'siglaUf', 'array[string]', null, 'Uma ou mais sigla(s) de unidades federativas (estados e Distrito Federal). Uma lista de siglas válidas pode ser obtida em /referencias/deputados/siglaUf. Se ausente, serão retornados deputados de todos os estados.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (972, 'siglaPartido', 'array[string]', null, 'Uma ou mais sigla(s) de partidos aos quais sejam filiados os deputados. Para obter as siglas válidas, consulte /partidos. Atenção: partidos diferentes podem usar a mesma sigla em diferentes legislaturas!');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (973, 'siglaSexo', 'string', null, 'Letra que designe o gênero dos parlamentares que se deseja buscar, sendo M para masculino e F para feminino');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (974, 'dataInicio', 'string', 'AAAA-MM-DD', 'Data de início de um intervalo de tempo, no formato AAAA-MM-DD.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (975, 'dataFim', 'string', 'AAAA-MM-DD', 'Data de término de um intervalo de tempo, no formato AAAA-MM-DD.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (976, 'id ', 'INTEGER', null, 'Identificador numérico do parlamentar.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (977, 'ano', 'array[integer]', null, 'Um ou mais ano(s) de ocorrência das despesas.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (978, 'mes', 'array[integer]', null, 'Um ou mais número(s) do(s) mês(es) de ocorrência das despesas.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (979, 'cnpjCpfFornecedor', 'string', null, 'CNPJ de uma pessoa jurídica, ou CPF de uma pessoa física, fornecedora do produto ou serviço (apenas números)');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (980, 'codTipoEvento', 'array[integer]', null, 'Um ou mais identificador(es) numérico(s) do(s) tipo(s) de evento que se deseja obter. Os valores válidos podem ser obtidos por uma requisição a /referencias/tiposEvento.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (981, 'codSituacao', 'array[integer]', null, 'Um ou mais identificador(es) numéricos de tipo(s) de situação de evento, separados por vírgula. Valores válidos podem ser obtidos em /referencias/situacoesEvento');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (982, 'codTipoOrgao', 'array[integer]', null, 'Um ou mais identificador(es) numérico(s) de tipo(s) de órgão(s) realizadores dos eventos que se deseja obter, separados por vírgula. Os valores válidos podem ser obtidos em /referencias/tiposOrgao.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (983, 'idOrgao', 'array[integer]', null, 'Um ou mais identificador(es) numérico(s) de órgão(s), separados por vírgula. Os identificadores podem ser obtidos em uma requisição a /orgaos.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (984, 'horaInicio', 'string', 'hh:mm', 'Hora inicial de um intervalo de tempo, no formato hh:mm, em horário de Brasília.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (985, 'horaFim', 'string', 'hh:mm', 'Hora final de um intervalo de tempo, no formato hh:mm, em horário de Brasília.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (986, 'id', 'INTEGER', null, 'O identificador numérico do evento do qual se deseja informações');
