INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (991, 'data', 'string', 'AAAA-MM-DD', 'Uma ou mais data(s) no formato AAAA-MM-DD, separadas por vírgulas. Se este parâmetro estiver presente, a requisição retornará as informações básicas sobre as legislaturas que estavam em curso nas datas informadas.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (992, 'id', 'INTEGER', null, 'Número da legislatura da qual se quer os dados.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (993, 'sigla', 'array[string]', null, 'Sigla de um ou mais partido(s), separadas por vírgulas.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (994, 'id', 'INTEGER', null, 'Id do Partido');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (995, 'siglaTipo', 'array[string]', null, 'Uma ou mais sigla(s) separadas por vírgulas do(s) tipo(s) das proposições que se deseja obter. A lista de tipos e siglas existentes pode ser obtida em /referencias/proposicoes/siglaTipo');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (996, 'numero', 'array[integer]', null, 'Um ou mais número(s), separados por vírgula, oficialmente atribuídos às proposições segundo o art. 137 do Regimento Interno, como "PL 1234/2016"');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (997, 'idDeputadoAutor', 'array[integer]', null, 'Um ou mais números identificador(es), separados por vírgula, do(s) deputado(s) autor(es) das proposições que serão listadas. Cada número deve ser o identificador exclusivo de um parlamentar no Dados Abertos');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (998, 'autor', 'string', null, 'Nome ou parte do nome do(s) autor(es) das proposições que se deseja obter. Deve estar entre aspas');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (999, 'siglaPartidoAutor', 'array[string]', null, 'Uma ou mais sigla(s) separadas por vírgulas do(s) partido(s) a que pertençam os autores das proposições a serem listadas');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (1000, 'idPartidoAutor', 'INTEGER', null, 'Identificador numérico no Dados Abertos do partido a que pertençam os autores das proposições que serão listadas. Esses identificadores podem ser obtidos em /partidos e são mais precisos do que as siglas, que podem ser usadas por partidos diferentes em épocas diferentes');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (1001, 'siglaUfAutor', 'array[string]', null, 'Uma ou mais sigla(s) de unidade(s) da federação (estados e Distrito Federal) pela(s) qual(quais) o(s) autor(es) das proposições selecionadas tenha(m) sido eleito(s)');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (1002, 'keywords', 'array[string]', null, 'Uma ou mais palavras chaves sobre o tema a que a proposição se relaciona');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (1003, 'tramitacaoSenado', 'boolean', null, 'Indicador booleano, com valor TRUE ou FALSE para trazer apenas proposições que já tenha tramitado no Senado');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (1004, 'dataApresentacaoInicio', 'string', 'AAAA-MM-DD', 'Data do início do intervalo de tempo em que tenham sido apresentadas as proposições a serem listadas, no formato AAAA-MM-DD');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (1005, 'dataApresentacaoFim', 'string', 'AAAA-MM-DD', 'Data do fim do intervalo de tempo em que tenham sido apresentadas as proposições a serem listadas');
