INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (1009, 'idProposicao', 'array[integer]', null, 'Um ou mais identificador(es) numéricos de proposições, que podem ser obtidos por meio do recurso /proposicoes. Se presente, listará as votações que tiveram a(s) proposição(ções) como objeto de votação ou que afetaram as proposições listadas.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (1010, 'idEvento', 'array[integer]', null, 'Identificador de um ou mais evento(s) realizado(s) na Câmara, separados por vírgula, nos quais tenham sido realizadas as votações a serem listadas. Os identificadores podem ser obtidos por meio do recurso /eventos. Somente os eventos deliberativos podem ter votações. Os eventos podem ter ocorrido fo');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (1011, 'id', 'string', null, 'Identificador alfanumérico da votação');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (1012, 'id', 'INTEGER', null, 'Identificador numérico do órgão');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (1013, 'idTipoEvento', 'array[integer]', null, 'Um ou mais identificador(es) numérico(s) de tipos de evento que se deseja obter. os valores válidos podem ser obtidos por uma requisição anterior a /referencias/tiposEvento.');
INSERT INTO apis.parameters (id, name, type, formart, description) VALUES (1014, 'id', 'string', null, 'Número da legislatura da qual se deseja os dados.');
