INSERT INTO apis.api_responses (id, description, metadata, data_dictionary, response_example) VALUES (412, 'Busca um projeto por meio de seu número PRONAC', null, null, '{"UF": "RJ", "area": "Humanidades", "nome": "VIRANDO OURO NAS REDES", "etapa": "PRE PRODUÇÃO - 30 DIAS: Contratação de profissionais que irão ministrar o curso, inscrições de alunos, orçamentos e compra de materias EXECUÇÃO: 180 DIAS: OFICINA 1 - Manifestações artisticas nas redes sociais: Plano de aula:&gt; 1)Introdução às redes sociais (Aula expositiva). Operação de de aplicativos (Aula Pratica) - OFICINA 2: Transformando Arte em profissão: Plano de aula 1( contar historias na internet). PÓS PRODUÇÃO - 30 DIAS: Prestação de contas ao Ministerio da Cultura.", "PRONAC": "233666", "_links": {"self": "http://api.salic.cultura.gov.br/v1/projetos/233666", "proponente": "http://api.salic.cultura.gov.br/v1/proponentes/523f817e26f95bf57701b4f1e7c775d1ac373f20065f2c128908cabf5b33", "fornecedores": "http://api.salic.cultura.gov.br/v1/fornecedores/?PRONAC=233666", "incentivadores": "http://api.salic.cultura.gov.br/v1/incentivadores/?PRONAC=233666"}, "cgccpf": "20884026000157", "resumo": "Formar e capacitar jovens e adultos tendo como foco quatro eixos tematicos comunicação, empreenddorismo, audiovisual e artistico a oficina será ministrada em oito meses com duas turmas com cerca de 40 alunos totalizando 80 jovens e adultos.", "sinopse": "Não se aplica ao projeto.", "segmento": "Ações de formação capacitação treinamento pessoal", "situacao": "Projeto em avaliação documental", "_embedded": {"captacoes": [], "divulgacao": [], "prorrogacao": [], "deslocamento": [], "distribuicao": [], "readequacoes": [], "marcas_anexadas": [], "relatorio_fisco": [], "relacao_pagamentos": [], "certidoes_negativas": [], "documentos_anexados": [], "relacao_bens_captal": []}, "mecanismo": "Mecenato", "municipio": "Niterói", "objetivos": "Objetivo Geral - O projeto tem como objetivo principal promover o desenvolvimento local integrado de comunidades com baixos indices de desenvolvimento humano carentes de ações sociais, culturais economicas, dar formação completa em empreendorismo artistico. Objetivo Especifico: A formação será realizada a partir de aulas expositivas, praticas e palestras com especialistas/referências em empreendorismo artistico, produção audivisual e marketing digital. Formação de agentes multiplicadores de informação.", "proponente": "INSTITUTO VIRADOURO PEDRO LEON MONASSA BESSIL", "ano_projeto": "23", "data_inicio": "2023-10-01", "providencia": "Proposta transformada em projeto e encaminhado para assinatura.", "data_termino": "2024-05-30", "enquadramento": "Artigo 26", "ficha_tecnica": "O DIRIGENTE DA INSTITUIÇÃO: É o responsavel pela gestão do projeto.Vai atuar na contratação de equipes de apoio, na definição dos orçamentos, no cronograma de trabalho e captação de recursos. O trabalho é voluntario não remunerado.COORDENAÇÃO GERAL: PAULA SANTANA - Iyalorixá idealizadora e presidente do Instituto Ose Dudú, presidente do Afoxé Omó Ifá Rio de Janeiro, vice presidente do IITAB (Instituto Irê de Tradições Afro-Brasileiras), Ativista, Palestrante, graduando em serviços social (Unopar), jornalista e colunista do Axé News e pesquisadora do Grupo Africanias da Escola de Música da UFRJ. PRODUTORA - PATRICIA HANNA - Pós graduanda em Gestão e Produção Cultural com experiencias profissionais que se articulam no campo da produção de eventos socioculturais, especialmente na area de literatura, e do marketing digital. COORDENAÇÃO DE PROJETOS: RAPHA LIMA COSTA - Pesquisador de Cultura e sociedade. Especialista em monitoramento de redes sociais (social listening) e netnografia. Analista de dados sênior. Curador de conhecimento. COORDENADOR DO PROJETO: RAPHAEL SILVA BARBOSA DE FARIA: Profissional de Midia, produtor com experiencia em marketing, publicidade, promoções e produção cultural, aplicativos office e sofwares e de gerencia de projetos.", "justificativa": "Durante a pandemia da COVID 19, foi observado entre os anos de 2020 e 2021 a mudança da forma com que os diferentes mercados se relacionam com seu público, dentre elas, podemos destacar o mercado das artes, que segundo pesquisas, durante a pandemis teve um crescimento de 46% em relação ao ano de 2019. Alguns especialistas apontam que esse crescimento se deve à crescente onda de vendas de obras artisticas pela internet. Também foi na pandemia que observamos artistas emergentes a crescer, impulsionados, pelas redes sociais. Pensando nesse panorama e no crescente mercado de venda de obras artisticas na internet e como podemos contribuir para artistas que não residem nos grandes centros urbanos. O mecanismo de Incentivo a Projetos Culturais na proposta 393.704 é importante na formação completa em empreendedorismo artístico e cultural.", "outras_fontes": 0, "valor_captado": 0, "valor_projeto": 0, "acessibilidade": "Acessibilidade Física: Na quadra da Unidos do Viradouro onde será realizado a oficina é adaptada as necessidades de portadores com deficiencia fisica e necessidades especiais é dotada de rampas, banheiro acessivel para facilitar o acesso de todos. Acessibilidade de Conteudo: Durante a realização da oficina haverá um interprete de libras para pessoas que desconhecem às linguagens de conteudos.", "democratizacao": "Democratização de acesso: Todas as vagas das oficinas serão gratuitas com carater social para comunidade. Ampliação de acesso: Art. 28 da IN nº01/2023 I-Doar 10% (dez por cento) dos produtos resultantes da execução do projeto para distribuição gratuita com carater social. IV-Disponibilizar, na internet, registros audiovisuais dos espetaculos, das exposições das atividades de ensino e de outros eventos referente a produção principal. VI-Realizar, gratuitamente, atividades paralelas aos projetos, tais como ensaios abertos, estagios, cursos, treinamentos, palestras, exposições, mostras e oficinas; X-Rodas de conversas", "valor_aprovado": 0, "valor_proposta": 945172.8, "valor_solicitado": 945172.8, "impacto_ambiental": "", "estrategia_execucao": "Todas as informações foram abordadas nos campos anteriores.", "especificacao_tecnica": "Não se aplica ao projeto."}');
